package web.com.springweb.vo;

public class Emp09 {
	// ctrl + shift + f
	private String ename;
	private String job;
	private double sal;
	public Emp09() {
		super();
		// TODO Auto-generated constructor stub
	}
	public Emp09(String ename, String job, double sal) {
		this.ename = ename;
		this.job = job;
		this.sal = sal;
	}
	public String getEname() {
		return ename;
	}
	public void setEname(String ename) {
		this.ename = ename;
	}
	public String getJob() {
		return job;
	}
	public void setJob(String job) {
		this.job = job;
	}
	public double getSal() {
		return sal;
	}
	public void setSal(double sal) {
		this.sal = sal;
	}
	

}
